import UIKit

class FDCartRouter: FDCartWireframeProtocol {
    
    weak var viewController: UIViewController?
    
    static func createModule() -> UIViewController {
        // Change to get view from storyboard if not using progammatic UI
        let view = FDCartViewController(nibName: nil, bundle: nil)
        let interactor = FDCartInteractor()
        let router = FDCartRouter()
        let presenter = FDCartPresenter(interface: view, interactor: interactor, router: router)
        
        view.presenter = presenter
        interactor.presenter = presenter
        router.viewController = view
        
        return view
    }
}
