import UIKit

class FDHomeRouter: FDHomeWireframeProtocol {
    
    weak var viewController: UIViewController?
    
    static func createModule() -> UIViewController {
        // Change to get view from storyboard if not using progammatic UI
        let view = FDHomeViewController(nibName: nil, bundle: nil)
        let interactor = FDHomeInteractor()
        let router = FDHomeRouter()
        let presenter = FDHomePresenter(interface: view, interactor: interactor, router: router)
        
        view.presenter = presenter
        interactor.presenter = presenter
        router.viewController = view
        
        return view
    }
    
    func navigateToCart() {
        let cartViewController = FDCartRouter.createModule()
        self.viewController?.navigationController?.pushViewController(cartViewController, animated: true)
//        if let viewController =
    }
}
